    fontSize: "18px",
    backgroundColor: "#6C63FF",
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
    transition: "0.3s ease all",
  },
  resultsContainer: {
    marginTop: "30px",
