    fontWeight: "bold",
  },
  errorHelp: {
    color: "#ccc",
    fontSize: "13px",
    margin: 0,
  },
  // Floating shapes
  floatingShape1: {
    position: "absolute",
    top: "10%",
    left: "5%",
    fontSize: "40px",
    opacity: 0.2,
    animation: "float 6s ease-in-out infinite",
  },
  floatingShape2: {
    position: "absolute",
    bottom: "15%",
    right: "10%",
    fontSize: "50px",
    opacity: 0.15,
    animation: "float 8s ease-in-out infinite",
  },
  floatingShape3: {
    position: "absolute",
    top: "20%",
    right: "20%",
    fontSize: "35px",
    opacity: 0.25,
